@layer base {
  * {
    @apply border-border;
  }

  html {
    scroll-behavior: smooth;
  }

  body {
    @apply bg-background text-foreground font-inter antialiased;
    background: linear-gradient(180deg, 
      hsl(0, 0%, 100%) 0%,
      hsl(210, 40%, 98%) 50%,
      hsl(214, 32%, 95%) 100%
    );
    min-height: 100vh;
  }
}

@layer components {
  .font-inter {
    font-family: 'Inter', sans-serif;
  }

  .premium-card {
    @apply bg-white/95 border border-gray-200/50 shadow-lg rounded-3xl;
    /* Removed backdrop-blur for performance, simplified shadows */
    box-shadow: 0 10px 25px -5px rgb(0 0 0 / 0.1), 0 4px 6px -2px rgb(0 0 0 / 0.05);
  }

  .hero-gradient {
    background: linear-gradient(135deg, 
      hsl(212, 100%, 97%) 0%,
      hsl(212, 100%, 95%) 25%,
      hsl(210, 100%, 96%) 50%,
      hsl(208, 100%, 97%) 75%,
      hsl(212, 100%, 98%) 100%
    );
  }

  .text-gradient-blue {
    background: linear-gradient(135deg, hsl(var(--cobalt)), hsl(var(--violet)));
    -webkit-background-clip: text;
    background-clip: text;
    -webkit-text-fill-color: transparent;
  }

  .text-gradient-gold {
    background: linear-gradient(135deg, hsl(var(--gold)), hsl(45, 93%, 65%));
    -webkit-background-clip: text;
    background-clip: text;
    -webkit-text-fill-color: transparent;
  }

  .text-gradient-brand {
    background: linear-gradient(135deg, hsl(var(--cobalt)), hsl(var(--violet)), hsl(var(--gold)));
    -webkit-background-clip: text;
    background-clip: text;
    -webkit-text-fill-color: transparent;
  }

  .btn-primary {
    background: linear-gradient(135deg, hsl(var(--cobalt)), hsl(var(--violet)));
    @apply text-white font-semibold px-6 py-3 lg:px-8 lg:py-4 rounded-2xl transition-all duration-200 shadow-lg hover:shadow-xl hover:scale-105;
  }

  .btn-primary:hover {
    background: linear-gradient(135deg, hsl(220, 91%, 22%), hsl(267, 47%, 28%));
  }

  .btn-secondary {
    @apply bg-white hover:bg-gray-50 font-semibold px-6 py-3 lg:px-8 lg:py-4 rounded-2xl border transition-all duration-200 shadow-lg hover:shadow-xl hover:scale-105;
    color: hsl(var(--cobalt));
    border-color: hsl(var(--cobalt-light));
  }

  .btn-accent {
    background: linear-gradient(135deg, hsl(var(--gold)), hsl(45, 93%, 65%));
    @apply text-gray-900 font-semibold px-6 py-3 lg:px-8 lg:py-4 rounded-2xl transition-all duration-200 shadow-lg hover:shadow-xl hover:scale-105;
  }

  .section-spacing {
    @apply py-16 lg:py-24 xl:py-32;
  }

  .container-custom {
    @apply max-w-6xl mx-auto px-4 sm:px-6 lg:px-8;
  }

  .fade-in-up {
    @apply animate-[fadeInUp_0.6s_ease-out_forwards] opacity-0;
  }

  .stagger-1 { animation-delay: 0.1s; }
  .stagger-2 { animation-delay: 0.2s; }
  .stagger-3 { animation-delay: 0.3s; }
  .stagger-4 { animation-delay: 0.4s; }

  .floating {
    @apply animate-[float_6s_ease-in-out_infinite];
  }

  .nav-blur {
    @apply backdrop-blur-xl bg-white/80 border-b border-gray-200/50;
  }

  .gradient-mesh {
    background: 
      radial-gradient(at 40% 20%, hsla(var(--cobalt), 0.15) 0px, transparent 50%),
      radial-gradient(at 80% 0%, hsla(var(--violet), 0.12) 0px, transparent 50%),
      radial-gradient(at 0% 50%, hsla(var(--cobalt), 0.1) 0px, transparent 50%),
      radial-gradient(at 80% 50%, hsla(var(--gold), 0.08) 0px, transparent 50%),
      radial-gradient(at 0% 100%, hsla(var(--violet), 0.1) 0px, transparent 50%),
      radial-gradient(at 80% 100%, hsla(var(--cobalt), 0.08) 0px, transparent 50%);
  }

  .bg-brand-gradient {
    background: linear-gradient(135deg, 
      hsl(var(--cobalt-light)) 0%, 
      hsl(var(--violet-light)) 50%, 
      hsl(var(--gold-light)) 100%);
  }

  .bg-cobalt-gradient {
    background: linear-gradient(135deg, hsl(var(--cobalt)), hsl(220, 91%, 35%));
  }

  .bg-violet-gradient {
    background: linear-gradient(135deg, hsl(var(--violet)), hsl(267, 47%, 42%));
  }

  .bg-gold-gradient {
    background: linear-gradient(135deg, hsl(var(--gold)), hsl(45, 93%, 68%));
  }

  /* Premium Hero Animations */
  @keyframes float-gentle {
    0%, 100% { transform: translateY(0px) rotate(0deg); }
    33% { transform: translateY(-10px) rotate(1deg); }
    66% { transform: translateY(-5px) rotate(-1deg); }
  }

  @keyframes pulse-soft {
    0%, 100% { opacity: 0.6; transform: scale(1); }
    50% { opacity: 0.8; transform: scale(1.05); }
  }

  @keyframes slide-in-up {
    0% { transform: translateY(60px); opacity: 0; }
    100% { transform: translateY(0); opacity: 1; }
  }

  @keyframes text-shimmer {
    0% { background-position: -200% center; }
    100% { background-position: 200% center; }
  }

  @keyframes card-hover-glow {
    0% { box-shadow: 0 10px 40px -10px rgba(0,0,0,0.1); }
    100% { box-shadow: 0 20px 60px -10px rgba(0,0,0,0.2); }
  }

  .float-gentle { animation: float-gentle 6s ease-in-out infinite; }
  .pulse-soft { animation: pulse-soft 3s ease-in-out infinite; }
  .slide-in-up { animation: slide-in-up 0.8s ease-out forwards; }

  .text-shimmer {
    background: linear-gradient(90deg, 
      hsl(var(--cobalt)) 0%, 
      hsl(var(--violet)) 25%, 
      hsl(var(--gold)) 50%, 
      hsl(var(--violet)) 75%, 
      hsl(var(--cobalt)) 100%);
    background-size: 200% auto;
    background-clip: text;
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    animation: text-shimmer 3s linear infinite;
  }

  .card-hover-glow:hover {
    animation: card-hover-glow 0.3s ease-out forwards;
  }

  .glass-premium {
    background: rgba(255, 255, 255, 0.25);
    backdrop-filter: blur(20px);
    border: 1px solid rgba(255, 255, 255, 0.3);
  }

  .button-magnetic {
    transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  }

  .button-magnetic:hover {
    transform: translateY(-3px) scale(1.02);
    box-shadow: 0 20px 40px rgba(0,0,0,0.15);
  }

  /* Premium AI Section Animations */
  @keyframes fade-in-up-elegant {
    0% { transform: translateY(40px); opacity: 0; }
    100% { transform: translateY(0); opacity: 1; }
  }

  @keyframes card-lift {
    0% { transform: translateY(0) scale(1); }
    100% { transform: translateY(-8px) scale(1.02); }
  }

  @keyframes icon-glow {
    0%, 100% { box-shadow: 0 8px 32px rgba(0,0,0,0.1); }
    50% { box-shadow: 0 12px 40px rgba(0,0,0,0.15); }
  }

  @keyframes gradient-shift {
    0% { background-position: 0% 50%; }
    50% { background-position: 100% 50%; }
    100% { background-position: 0% 50%; }
  }

  .fade-in-up-elegant {
    animation: fade-in-up-elegant 0.8s ease-out forwards;
    opacity: 0;
  }

  .stagger-delay-1 { animation-delay: 0.1s; }
  .stagger-delay-2 { animation-delay: 0.2s; }
  .stagger-delay-3 { animation-delay: 0.3s; }
  .stagger-delay-4 { animation-delay: 0.4s; }
  .stagger-delay-5 { animation-delay: 0.5s; }
  .stagger-delay-6 { animation-delay: 0.6s; }

  .card-lift-hover:hover {
    animation: card-lift 0.3s ease-out forwards;
  }

  .icon-glow-hover:hover {
    animation: icon-glow 2s ease-in-out infinite;
  }

  .gradient-accent {
    background: linear-gradient(-45deg, hsl(220, 91%, 50%), hsl(267, 47%, 50%), hsl(45, 93%, 50%), hsl(220, 91%, 50%));
    background-size: 400% 400%;
    animation: gradient-shift 4s ease infinite;
  }

  .elegant-underline {
    position: relative;
    overflow: hidden;
  }

  .elegant-underline::after {
    content: '';
    position: absolute;
    bottom: -4px;
    left: 50%;
    width: 0;
    height: 2px;
    background: linear-gradient(90deg, hsl(220, 91%, 50%), hsl(267, 47%, 50%));
    transition: all 0.5s ease;
    transform: translateX(-50%);
  }

  .elegant-underline:hover::after {
    width: 100%;
  }
}

@keyframes fadeInUp {
  from {
    opacity: 0;
    transform: translateY(30px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

@keyframes float {
  0%, 100% {
    transform: translateY(0px);
  }
  50% {
    transform: translateY(-20px);
  }
}

/* Enhanced Hero Animations */
.floating-card {
  animation: floatingCard 4s ease-in-out infinite;
  transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
}

.floating-card:hover {
  transform: translateY(-5px) scale(1.05);
  box-shadow: 0 20px 40px rgba(0, 0, 0, 0.15);
}

@keyframes floatingCard {
  0%, 100% { transform: translateY(0px) rotate(0deg); }
  33% { transform: translateY(-8px) rotate(1deg); }
  66% { transform: translateY(4px) rotate(-1deg); }
}

.parallax-slow {
  animation: parallaxSlow 20s linear infinite;
}

.parallax-medium {
  animation: parallaxMedium 15s linear infinite;
}

.parallax-fast {
  animation: parallaxFast 10s linear infinite;
}

@keyframes parallaxSlow {
  0% { transform: translateY(0px) translateX(0px); }
  25% { transform: translateY(-10px) translateX(5px); }
  50% { transform: translateY(0px) translateX(10px); }
  75% { transform: translateY(10px) translateX(5px); }
  100% { transform: translateY(0px) translateX(0px); }
}

@keyframes parallaxMedium {
  0% { transform: translateY(0px) translateX(0px) rotate(0deg); }
  50% { transform: translateY(-15px) translateX(-8px) rotate(2deg); }
  100% { transform: translateY(0px) translateX(0px) rotate(0deg); }
}

@keyframes parallaxFast {
  0% { transform: translateY(0px) scale(1); }
  50% { transform: translateY(-20px) scale(1.05); }
  100% { transform: translateY(0px) scale(1); }
}

.hero-image-glow {
  position: relative;
  overflow: hidden;
}

.hero-image-glow::before {
  content: '';
  position: absolute;
  inset: -20px;
  background: linear-gradient(45deg, #3b82f6, #8b5cf6, #06b6d4, #3b82f6);
  border-radius: 32px;
  opacity: 0.6;
  filter: blur(20px);
  animation: glowPulse 4s ease-in-out infinite;
  z-index: -1;
}

@keyframes glowPulse {
  0%, 100% { opacity: 0.4; transform: scale(0.95); }
  50% { opacity: 0.8; transform: scale(1.05); }
}

.text-glow {
  text-shadow: 0 0 20px rgba(59, 130, 246, 0.3);
}

.button-hover-effect {
  position: relative;
  overflow: hidden;
  transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
}

.button-hover-effect::before {
  content: '';
  position: absolute;
  top: 0;
  left: -100%;
  width: 100%;
  height: 100%;
  background: linear-gradient(90deg, transparent, rgba(255, 255, 255, 0.2), transparent);
  transition: left 0.5s;
}

.button-hover-effect:hover::before {
  left: 100%;
}

.button-hover-effect:hover {
  transform: translateY(-2px);
  box-shadow: 0 10px 30px rgba(0, 0, 0, 0.2);
}

.gradient-text-animate {
  background: linear-gradient(45deg, #3b82f6, #8b5cf6, #06b6d4, #3b82f6);
  background-size: 300% 300%;
  background-clip: text;
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  animation: gradientShift 3s ease-in-out infinite;
}

/* 10X Sophisticated Animations */
.stagger-fade-up {
  opacity: 0;
  transform: translateY(40px) scale(0.98);
  animation: fadeUpStagger 1.2s cubic-bezier(0.4, 0, 0.2, 1) forwards;
}

@keyframes fadeUpStagger {
  to {
    opacity: 1;
    transform: translateY(0) scale(1);
  }
}

/* Enhanced floating cards with shimmer effect */
.floating-card::before {
  content: '';
  position: absolute;
  top: 0;
  left: -100%;
  width: 100%;
  height: 100%;
  background: linear-gradient(90deg, transparent, rgba(255,255,255,0.4), transparent);
  transition: left 0.8s ease;
  z-index: 10;
}

.floating-card:hover::before {
  left: 100%;
}

.floating-card:hover {
  transform: translateY(-12px) scale(1.02);
  box-shadow: 0 32px 64px -12px rgba(0, 0, 0, 0.18), 0 0 0 1px rgba(255, 255, 255, 0.05);
}

/* Sophisticated number animations */
.animate-number {
  background: linear-gradient(135deg, #1e293b, #475569, #64748b);
  background-size: 200% 200%;
  animation: shimmer 3s ease-in-out infinite;
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
}

@keyframes shimmer {
  0%, 100% { background-position: 0% 50%; }
  50% { background-position: 100% 50%; }
}

/* Elegant progress bars with shine */
.premium-progress {
  position: relative;
  overflow: hidden;
}

.premium-progress::after {
  content: '';
  position: absolute;
  top: 0;
  left: -100%;
  width: 100%;
  height: 100%;
  background: linear-gradient(90deg, transparent, rgba(255,255,255,0.6), transparent);
  animation: progressShine 2s ease-in-out infinite;
}

@keyframes progressShine {
  0% { left: -100%; }
  50% { left: 100%; }
  100% { left: -100%; }
}

/* Enhanced premium cards with radial glow */
.premium-card::after {
  content: '';
  position: absolute;
  top: -50%;
  left: -50%;
  width: 200%;
  height: 200%;
  background: radial-gradient(circle, rgba(59, 130, 246, 0.1) 0%, transparent 70%);
  opacity: 0;
  transition: opacity 0.6s ease;
  pointer-events: none;
  z-index: -1;
}

.premium-card:hover::after {
  opacity: 1;
}

.premium-card:hover {
  /* Simplified hover effects for better performance */
  box-shadow: 0 15px 35px rgba(31, 38, 135, 0.1), 0 5px 15px rgba(31, 38, 135, 0.08);
  transform: translateY(-4px);
  /* Removed scale for performance */
}

/* Magnetic hover effects */
.magnetic-card {
  transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  cursor: pointer;
}

.magnetic-card:hover {
  /* Simplified for performance */
  transform: translateY(-2px);
  filter: brightness(1.02);
}

/* Luxury text effects */
.luxury-text {
  background: linear-gradient(135deg, #1e293b 0%, #334155 50%, #475569 100%);
  background-size: 200% 200%;
  animation: luxuryShimmer 4s ease-in-out infinite;
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
}

@keyframes luxuryShimmer {
  0%, 100% { background-position: 0% 50%; }
  50% { background-position: 100% 50%; }
}

/* Subtle glow effects */
.subtle-glow {
  box-shadow: 0 0 20px rgba(59, 130, 246, 0.1), 0 0 40px rgba(59, 130, 246, 0.05);
  transition: box-shadow 0.6s ease;
}

.subtle-glow:hover {
  box-shadow: 0 0 30px rgba(59, 130, 246, 0.15), 0 0 60px rgba(59, 130, 246, 0.1);
}

/* Breath animation for special elements */
.breath-animation {
  animation: breathe 4s ease-in-out infinite;
}

@keyframes breathe {
  0%, 100% { transform: scale(1); }
  50% { transform: scale(1.02); }
}

/* Scrolling animation for industries list */
.animate-scroll {
  animation: smoothScroll 20s linear infinite;
}

@keyframes smoothScroll {
  0% { transform: translateY(0); }
  100% { transform: translateY(-50%); }
}

@keyframes gradientShift {
  0%, 100% { background-position: 0% 50%; }
  50% { background-position: 100% 50%; }
}

/* Advanced AI Section Animations */
.counter-animation {
  --counter-value: 0;
  animation: counterUp 2s ease-out forwards;
}

@keyframes counterUp {
  from { --counter-value: 0; }
  to { --counter-value: 1; }
}

/* Removed loud animations for elegant design */

.stagger-fade-up {
  opacity: 0;
  transform: translateY(30px);
  animation: fadeInUp 0.8s ease-out forwards;
}

.stagger-1 { animation-delay: 0.1s; }
.stagger-2 { animation-delay: 0.3s; }
.stagger-3 { animation-delay: 0.5s; }
.stagger-4 { animation-delay: 0.7s; }
.stagger-5 { animation-delay: 0.9s; }
.stagger-6 { animation-delay: 1.1s; }

.morphing-bg {
  background: linear-gradient(45deg, #ff6b6b, #4ecdc4, #45b7d1, #96ceb4, #ffeaa7);
  background-size: 300% 300%;
  animation: morphBg 8s ease-in-out infinite;
}

@keyframes morphBg {
  0%, 100% { background-position: 0% 50%; }
  25% { background-position: 100% 0%; }
  50% { background-position: 100% 100%; }
  75% { background-position: 0% 100%; }
}

.slide-in-scale {
  transform: translateX(-100%) scale(0.8);
  animation: slideInScale 0.8s cubic-bezier(0.23, 1, 0.32, 1) forwards;
}

@keyframes slideInScale {
  to {
    transform: translateX(0) scale(1);
  }
}

/* Removed typewriter effect for cleaner look */

.progress-bar {
  position: relative;
  background: #e5e7eb;
  border-radius: 10px;
  overflow: hidden;
}

.progress-fill {
  height: 100%;
  background: linear-gradient(90deg, #ef4444, #f97316, #eab308);
  border-radius: 10px;
  animation: progressFill 3s ease-out forwards;
  transform-origin: left;
}

@keyframes progressFill {
  from { transform: scaleX(0); }
  to { transform: scaleX(1); }
}

.glass-card {
  background: rgba(255, 255, 255, 0.1);
  backdrop-filter: blur(20px);
  border: 1px solid rgba(255, 255, 255, 0.2);
  box-shadow: 0 8px 32px rgba(0, 0, 0, 0.1);
}

/* Enhanced premium animations */
@keyframes spin-slow {
  from {
    transform: rotate(0deg);
  }
  to {
    transform: rotate(360deg);
  }
}

.animate-spin-slow {
  animation: spin-slow 20s linear infinite;
}

@keyframes float-gentle {
  0%, 100% {
    transform: translateY(0px) translateX(0px) scale(1);
  }
  25% {
    transform: translateY(-10px) translateX(2px) scale(1.02);
  }
  50% {
    transform: translateY(-5px) translateX(-2px) scale(0.98);
  }
  75% {
    transform: translateY(-15px) translateX(1px) scale(1.01);
  }
}

.animate-float-gentle {
  animation: float-gentle 6s ease-in-out infinite;
}

@keyframes premium-pulse {
  0%, 100% {
    transform: scale(1);
    opacity: 0.8;
  }
  50% {
    transform: scale(1.05);
    opacity: 1;
  }
}

.premium-pulse {
  animation: premium-pulse 3s ease-in-out infinite;
}

