// Email notification service using SendGrid
// Reference: javascript_sendgrid integration

import sgMail from '@sendgrid/mail';

// Initialize SendGrid with API key if available
const SENDGRID_API_KEY = process.env.SENDGRID_API_KEY;
const ADMIN_EMAIL = 'leadcrestconsulting6@gmail.com';
const FROM_EMAIL = 'leadcrestconsulting6@gmail.com'; // Using verified Gmail address as sender

if (SENDGRID_API_KEY) {
  sgMail.setApiKey(SENDGRID_API_KEY);
}

interface ContactEmailData {
  name: string;
  email: string;
  phone: string;
  whoIsThisFor: string;
}

interface BookingConfirmationData {
  fullName: string;
  email: string;
  mobile: string;
  packageName: string;
  bookingType: 'discovery_call' | 'investment';
  amount?: number;
  currentStage: string;
}

interface ResourceDownloadData {
  fullName: string;
  email: string;
  mobile: string;
  resourceTitle: string;
  currentStage: string;
}

// Helper function to send emails
async function sendEmail(to: string | string[], subject: string, text: string, html: string): Promise<boolean> {
  if (!SENDGRID_API_KEY) {
    const recipients = Array.isArray(to) ? to.length : 1;
    console.log(`‚ö†Ô∏è  SendGrid not configured. Email would have been sent to ${recipients} recipient(s)`);
    console.log('Subject:', subject);
    console.log('‚úâÔ∏è  Email notification skipped due to missing configuration');
    return false;
  }

  try {
    const msg = {
      to: Array.isArray(to) ? to : [to],
      from: FROM_EMAIL,
      subject: subject,
      text: text,
      html: html
    };

    await sgMail.send(msg);
    console.log(`‚úÖ Email sent successfully to ${Array.isArray(to) ? to.join(', ') : to}`);
    return true;
  } catch (error: any) {
    console.error('‚ùå Error sending email:', error);
    if (error.response && error.response.body) {
      console.error('SendGrid error details:', JSON.stringify(error.response.body, null, 2));
    }
    return false;
  }
}

