import twilio from 'twilio';

// Initialize Twilio client
const accountSid = process.env.TWILIO_ACCOUNT_SID;
const authToken = process.env.TWILIO_AUTH_TOKEN;
const twilioPhoneNumber = process.env.TWILIO_PHONE_NUMBER;
const adminPhoneNumber = process.env.ADMIN_PHONE_NUMBER || '+911234567890'; // Default admin phone

// Create Twilio client only if credentials are provided
const twilioClient = accountSid && authToken ? twilio(accountSid, authToken) : null;

interface ContactSMSData {
  name: string;
  email: string;
  phone: string;
  whoIsThisFor: string;
}

interface BookingSMSData {
  fullName: string;
  mobile: string;
  packageName: string;
  bookingType: 'discovery_call' | 'investment';
  amount?: number;
  currentStage: string;
}

interface PaymentSMSData {
  customerName: string;
  packageName: string;
  amount: number;
  status: 'completed' | 'failed';
}

// Helper function to format phone numbers to E.164 format
function formatPhoneNumber(phone: string): string {
  // Remove all non-digit characters
  let cleaned = phone.replace(/\D/g, '');

  // If the number doesn't start with country code, assume it's Indian (+91)
  if (!cleaned.startsWith('91') && cleaned.length === 10) {
    cleaned = '91' + cleaned;
  }

  // Add the + prefix
  return '+' + cleaned;
}

// Send SMS to admin when a new contact form is submitted
export async function sendContactNotificationSMS(data: ContactSMSData): Promise<boolean> {
  try {
    const message = `New Contact Form:
Name: ${data.name}
Phone: ${data.phone}
Stage: ${data.whoIsThisFor}
Email: ${data.email}`;

    console.log('\nüì± Sending SMS notification to admin...');

    if (!twilioClient || !twilioPhoneNumber) {
      console.log('‚ö†Ô∏è  Twilio not configured. SMS notification skipped.');
      console.log('SMS Content:', message);
      return false;
    }

    const sms = await twilioClient.messages.create({
      body: message,
      from: twilioPhoneNumber,
      to: formatPhoneNumber(adminPhoneNumber)
    });

    console.log(`‚úÖ SMS sent successfully! Message SID: ${sms.sid}`);
    return true;
  } catch (error) {
    console.error('‚ùå Error sending SMS:', error);
    return false;
  }
}

// Send SMS confirmation to customer for bookings
export async function sendBookingConfirmationSMS(data: BookingSMSData): Promise<boolean> {
  try {
    const customerMessage = data.bookingType === 'discovery_call' 
      ? `Hi ${data.fullName}, your discovery call for ${data.packageName} has been booked. We'll contact you within 24 hours. - Leadcrest Consulting`
      : `Hi ${data.fullName}, thank you for investing in ${data.packageName} (‚Çπ${data.amount?.toLocaleString('en-IN')}). Our team will reach out soon. - Leadcrest Consulting`;

    const adminMessage = `New ${data.bookingType === 'discovery_call' ? 'Discovery Call' : 'Investment'} Booking:
Name: ${data.fullName}
Mobile: ${data.mobile}
Package: ${data.packageName}
${data.amount ? `Amount: ‚Çπ${data.amount.toLocaleString('en-IN')}` : ''}`;

    console.log('\nüì± Sending booking SMS notifications...');

    if (!twilioClient || !twilioPhoneNumber) {
      console.log('‚ö†Ô∏è  Twilio not configured. SMS notifications skipped.');
      console.log('Customer SMS:', customerMessage);
      console.log('Admin SMS:', adminMessage);
      return false;
    }

    // Send to customer
    try {
      const customerSMS = await twilioClient.messages.create({
        body: customerMessage,
        from: twilioPhoneNumber,
        to: formatPhoneNumber(data.mobile)
      });
      console.log(`‚úÖ Customer SMS sent! SID: ${customerSMS.sid}`);
    } catch (error) {
      console.error('‚ö†Ô∏è  Could not send SMS to customer:', error);
    }

    // Send to admin
    try {
      const adminSMS = await twilioClient.messages.create({
        body: adminMessage,
        from: twilioPhoneNumber,
        to: formatPhoneNumber(adminPhoneNumber)
      });
      console.log(`‚úÖ Admin SMS sent! SID: ${adminSMS.sid}`);
    } catch (error) {
      console.error('‚ö†Ô∏è  Could not send SMS to admin:', error);
    }

    return true;
  } catch (error) {
    console.error('‚ùå Error in SMS service:', error);
    return false;
  }
}

// Send SMS notification for payment status
export async function sendPaymentStatusSMS(data: PaymentSMSData): Promise<boolean> {
  try {
    const adminMessage = data.status === 'completed'
      ? `üí∞ Payment Received!\nCustomer: ${data.customerName}\nPackage: ${data.packageName}\nAmount: ‚Çπ${data.amount.toLocaleString('en-IN')}`
      : `‚ö†Ô∏è Payment Failed\nCustomer: ${data.customerName}\nPackage: ${data.packageName}\nAmount: ‚Çπ${data.amount.toLocaleString('en-IN')}`;

    console.log('\nüì± Sending payment SMS notification...');

    if (!twilioClient || !twilioPhoneNumber) {
      console.log('‚ö†Ô∏è  Twilio not configured. SMS notification skipped.');
      console.log('SMS Content:', adminMessage);
      return false;
    }

    const sms = await twilioClient.messages.create({
      body: adminMessage,
      from: twilioPhoneNumber,
      to: formatPhoneNumber(adminPhoneNumber)
    });

    console.log(`‚úÖ Payment SMS sent! SID: ${sms.sid}`);
    return true;
  } catch (error) {
    console.error('‚ùå Error sending payment SMS:', error);
    return false;
  }
}

// Send custom SMS to any number
export async function sendCustomSMS(to: string, message: string): Promise<boolean> {
  try {
    console.log('\nüì± Sending custom SMS...');

    if (!twilioClient || !twilioPhoneNumber) {
      console.log('‚ö†Ô∏è  Twilio not configured. SMS not sent.');
      console.log(`To: ${to}`);
      console.log(`Message: ${message}`);
      return false;
    }

    const sms = await twilioClient.messages.create({
      body: message,
      from: twilioPhoneNumber,
      to: formatPhoneNumber(to)
    });

    console.log(`‚úÖ Custom SMS sent! SID: ${sms.sid}`);
    return true;
  } catch (error) {
    console.error('‚ùå Error sending custom SMS:', error);
    return false;
  }
}

// Bulk SMS sending for marketing campaigns
export async function sendBulkSMS(numbers: string[], message: string): Promise<{ sent: number; failed: number; results: Array<{ number: string; status: string; error?: string }> }> {
  const results: Array<{ number: string; status: string; error?: string }> = [];
  let sent = 0;
  let failed = 0;

  if (!twilioClient || !twilioPhoneNumber) {
    console.log('‚ö†Ô∏è  Twilio not configured. Bulk SMS not sent.');
    return { sent: 0, failed: numbers.length, results: numbers.map(n => ({ number: n, status: 'failed', error: 'Twilio not configured' })) };
  }

  for (const number of numbers) {
    try {
      const sms = await twilioClient.messages.create({
        body: message,
        from: twilioPhoneNumber,
        to: formatPhoneNumber(number)
      });

      results.push({ number, status: 'sent' });
      sent++;
      console.log(`‚úÖ SMS sent to ${number}: ${sms.sid}`);
    } catch (error: any) {
      results.push({ number, status: 'failed', error: error.message });
      failed++;
      console.error(`‚ùå Failed to send to ${number}:`, error.message);
    }
  }

  console.log(`\nüìä Bulk SMS Summary: Sent: ${sent}, Failed: ${failed}`);
  return { sent, failed, results };
}

server/openaiService.ts.content:
import OpenAI from "openai";

// the newest OpenAI model is "gpt-5" which was released August 7, 2025. do not change this unless explicitly requested by the user
const openai = new OpenAI({ apiKey: process.env.OPENAI_API_KEY });

export interface BlogGenerationRequest {
  topic: string;
  keywords?: string[];
  tone?: 'professional' | 'casual' | 'educational' | 'conversational';
  length?: 'short' | 'medium' | 'long';
  category?: string;
}

export interface GeneratedBlogContent {
  title: string;
  excerpt: string;
  content: string;
  category: string;
  imageUrl: string;
}

export async function generateBlogPost(request: BlogGenerationRequest): Promise<GeneratedBlogContent> {
  try {
    const { topic, keywords = [], tone = 'professional', length = 'medium', category = 'Career Development' } = request;

    // Generate word count based on length
    const wordCounts = {
      short: '800-1000 words',
      medium: '1500-2000 words', 
      long: '2500-3000 words'
    };

    const keywordText = keywords.length > 0 ? `Keywords to include: ${keywords.join(', ')}` : '';

    const prompt = `Write a comprehensive blog post for Leadcrest Consulting, a career counseling and education guidance company with 24+ years of experience.

Topic: ${topic}
${keywordText}
Tone: ${tone}
Length: ${wordCounts[length]}
Category: ${category}

The blog should be written from the perspective of Sumanta Chaudhuri, the founder with extensive leadership experience at global MNCs including Reliance Industries Ltd, Vodafone, and Aircel.

Please respond with a JSON object containing:
{
  "title": "Engaging, SEO-friendly title",
  "excerpt": "Compelling 2-3 sentence summary",
  "content": "Full blog post content in HTML format with proper headings, paragraphs, lists, and formatting. Include practical advice, examples, and actionable insights.",
  "category": "${category}",
  "imageUrl": "https://images.unsplash.com/photo-1522202176988-66273c2fd55f?ixlib=rb-4.0.3&auto=format&fit=crop&w=1200&q=80"
}

Make sure the content is:
- Professionally written and engaging
- Includes practical career advice
- Has proper HTML formatting with h2, h3 headings
- Contains actionable insights and tips
- References modern career challenges and digital transformation
- Maintains a helpful, expert tone throughout`;

    const response = await openai.chat.completions.create({
      model: "gpt-4o", // Use a stable, available OpenAI model
      messages: [
        {
          role: "system",
          content: "You are an expert content writer specializing in career development and professional growth. Write comprehensive, valuable blog posts that help professionals advance their careers."
        },
        {
          role: "user",
          content: prompt
        }
      ],
      response_format: { type: "json_object" },
      temperature: 0.7
    });

    const result = JSON.parse(response.choices[0].message.content || '{}');

    // Validate the response has required fields
    if (!result.title || !result.excerpt || !result.content) {
      throw new Error('AI response missing required fields');
    }

    return {
      title: result.title,
      excerpt: result.excerpt,
      content: result.content,
      category: result.category || category,
      imageUrl: result.imageUrl || 'https://images.unsplash.com/photo-1522202176988-66273c2fd55f?ixlib=rb-4.0.3&auto=format&fit=crop&w=1200&q=80'
    };

  } catch (error: any) {
    console.error('AI blog generation error:', error);
    throw new Error(`Failed to generate blog post: ${error.message}`);
  }
}

export async function improveBlogContent(existingContent: string, improvements: string): Promise<string> {
  try {
    const prompt = `Improve the following blog post content based on these specific requests: ${improvements}

Current content:
${existingContent}

Please respond with the improved content in HTML format, maintaining the original structure but incorporating the requested improvements.`;

    const response = await openai.chat.completions.create({
      model: "gpt-4o", // Use a stable, available OpenAI model
      messages: [
        {
          role: "system",
          content: "You are an expert content editor. Improve blog posts while maintaining their core message and structure."
        },
        {
          role: "user",
          content: prompt
        }
      ],
      temperature: 0.3
    });

    return response.choices[0].message.content || existingContent;

  } catch (error: any) {
    console.error('AI content improvement error:', error);
    throw new Error(`Failed to improve content: ${error.message}`);
  }
}

